import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

/**
 * @Author Lichee
 * @Date 2020/9/17 15:30
 * @Version 1.0
 * <p>
 * https://leetcode.com/explore/interview/card/top-interview-questions-easy/127/strings/881/
 * First Unique Character in a String
 */
public class FirstUniqueCharacterinaString {
    public static void main(String[] args) {
//        String s = "";
        String s = "leetcodel";
        System.out.println(firstUniqChar2(s));
    }

    // 这个方法超时了
    public static int firstUniqChar(String s) {
        int i = 0;
        String keep = s;
        while (i < s.length() && (s.lastIndexOf(s.charAt(i)) != i)) {
            String newS = "";
            for (int j = i + 1; j < s.length(); j++) {
                if (s.charAt(j) != s.charAt(i)) {
                    newS += s.charAt(j);
                }
            }
            s = newS;
            i = 0;
        }
        if (i == s.length()) return -1;
        return keep.indexOf(s.charAt(i));
    }

    //
    public static int firstUniqChar2(String s) {
        int i=0;
        List<Character> duplist = new ArrayList<>(s.length());
        while (i<s.length()){
            if((s.lastIndexOf(s.charAt(i))==i)&&(!duplist.contains(s.charAt(i)))){
                break;
            }
            duplist.add(s.charAt(i));
            i++;
        }
        if(i==s.length()) return -1;
        return i;
    }
}
